defined('_JEXEC') or die('Restricted access');

jimport('joomla.application.component.controller');

class vendorsordercentreController extends JController
{

	function __construct()
	{
		parent::__construct();
	}

	function display()
	{
		
		parent::display();
	}
	
	//function to display vendor orders
	function vendorsordercentre()
	{
		JRequest::setVar('view', 'vendorsordercentre');
		JRequest::setVar('task', 'vendorsordercentre');
		parent::display();
		
	}
	
	
	//function to display vendor billing form
	function vendororderinfo()
	{
		JRequest::getVar('view','vendorsordercentre');
		JRequest::setVar('task', 'vendororderinfo');
		parent::display();
	}
	
	//function to remove single order
	function remove()
	{ 		
		$id = JRequest::getVar("cid");
		
	    $db = JFactory::getDBO();
		$k=0;
		
		for($i=0;$i<count($id);$i++)
		{
		$sql = 'DELETE  FROM `#__cam_vendor_billing` where id='.$id[$i];
		$db->setQuery($sql);
		if($db->query())
		$k++;
		}
		$msg = $k." record(s) deleted";
		$this->setRedirect("index.php?option=com_camassistant&controller=vendorsordercentre&task=vendorsordercentre",$msg);
	}
	
	
}
