function validatewp()
{
	var chks = document.getElementsByName('work_perform[]');
	var hasChecked = false;
	for (var i = 0; i < chks.length; i++)
	{
	if (chks[i].checked)
	{
		hasChecked = true;
		break;
	}
	}
	if (hasChecked == false)
	return false;
	else
	return true;
}
