// no direct access
defined('_JEXEC') or die('Restricted access');

jimport( 'joomla.application.component.view' );

class feedbackViewFeedback extends Jview
{
	function display($tpl = null){
		$model =& JRequest::getVar('model');
		$order_by = JRequest::getVar('order','submitted_ts');
		$dir = JRequest::getVar('dir','asc');
		$feedback =& $model->getFeedback($order_by, $dir);
		$pagination =& $model->getPagination();
		
		JToolBarHelper::title(JText::_('Feedback Manager'), 'generic.png' );
        JToolBarHelper::deleteList();
        JToolBarHelper::editListX();
        //JToolBarHelper::addNewX();
		
		$params = JComponentHelper::getParams('com_feedback');
		
		$this->assignRef('feedback', $feedback);
		$this->assignRef('pagination', $pagination);
		$this->assignRef('order', $order_by);
		$this->assignRef('dir', $dir);
		$this->assignRef('params', $params);
		
		parent::display($tpl);
	}

}
?>