	 function getopenfiles($path)
	 {
		$dir=dir($path);
		while($filename=$dir->read())
		{
			if($filename!='.'&&$filename!='..')
			{
			if(strpos($filename,"."))
			$data['files'][]=$filename;
			else
			$data['folders'][]= $filename;
			}
		}
		$dir->close();
		return $data;
	 }

