// no direct access
defined('_JEXEC') or die('Restricted access');

jimport( 'joomla.application.component.view' );
require_once(JPATH_COMPONENT . DS . 'models' . DS . 'boardmembers.php');
//class vendorsViewvendors extends Jview
class boardmembersViewaddboardmember extends Jview
{
	function display($tpl = null){
		// global $mainframe;
		$task = JRequest::getVar("task",'');
		
		$model = new boardmembersModelboardmembers();
		$states = $model->getstates();
         
		$this->assignRef('states',	$states);

		$rows=$model->getProperties();
		$row1[0] = new stdClass();
		$row1[0]->value = "0";
		$row1[0]->text = "Select Property";
		$rows=array_merge($row1,$rows);
		if($task == 'editboardmember')
		{
		$model = new boardmembersModelboardmembers();
		$details = $model->editboardmember();
		$this->assignRef('details',$details[0]);
		$states = $model->getstates();
		$this->assignRef('states', $states);
        
		}
		
		$this->assignRef('propertyList',$rows);
		parent::display($tpl);

	}

}
