function add_commas(x,id)
{
	var val = parseFloat(x).toFixed(2)+'',
      rgx = /(\d+)(\d{3}[\d,]*\.\d{2})/;
  while (rgx.test(val)) {
    val = val.replace(rgx, '$1' + ',' + '$2');
  }
document.getElementById(id).value = val;
}
