// no direct access
defined('_JEXEC') or die('Restricted access');

jimport( 'joomla.application.component.view' );

class assignedboardmembersViewAssignedboardmembers extends Jview
{

	function display($tpl = null){
	$task=JRequest::getVar('task','');

	$propertynames = $this->get('propertynames');
	$this->assignRef('propertynames', $propertynames);
	
	if($task=='addmember')
	$this->setLayout('addmember');	

	$boardmembers = $this->get('boardmembers');
	$this->assignRef('boardmembers', $boardmembers);
	
	$details = $this->get('details');
	$this->assignRef('details', $details);

	$pname = $this->get('editmembers');
	$this->assignRef('editmembers', $pname);
	
	$mname = $this->get('editmembers1');
	$this->assignRef('editmembers1', $mname);
	

	
	if($task=='editmembers')
	$this->setLayout('editmember');	

		parent::display($tpl);
	}
		
}

?>