// no direct access
defined('_JEXEC') or die('Restricted access');

jimport( 'joomla.application.component.view' );

class invitationcodesViewinvitationcodes extends Jview
{
	function display($tpl = null){

			
			$task = JRequest::getVar("task",'');
			$model = $this->getModel('invitationcodes');
			if($task == 'vendorcode'){
			$manager_info = $model->managerinfo();
			$this->assignRef('manager_info',	$manager_info);
			$this->setLayout('vendorcode');
			
			parent::display($tpl);
			}
			else if($task == 'vendortext'){
			$this->setLayout('vendor_text');
			
			parent::display($tpl);
			}
			
			else{
			parent::display($tpl);
			}		
			}
		

	}
?>