<script type="text/javascript" src="components/com_camassistant/skin/js/jquery-1.4.4.min.js"></script>
<script type="text/javascript">
var checkboxHeight = "25";
var radioHeight = "25";
var selectWidth = "190";

document.write('<style type="text/css">input.styled { display: none; } select.styled { position: relative; width: ' + selectWidth + 'px; opacity: 0; filter: alpha(opacity=0); z-index: 5; } .disabled { opacity: 0.5; filter: alpha(opacity=50); }</style>');

var Custom = {
	init: function() {
		var inputs = document.getElementsByTagName("input"), span = Array(), textnode, option, active;
		for(a = 0; a < inputs.length; a++) {
			if((inputs[a].type == "checkbox" || inputs[a].type == "radio") && inputs[a].className.indexOf("styled") > -1) {
				span[a] = document.createElement("span");
				span[a].className = inputs[a].type;

				if(inputs[a].checked == true) {
					if(inputs[a].type == "checkbox") {
						position = "0 -" + (checkboxHeight*2) + "px";
						span[a].style.backgroundPosition = position;
					} else {
						position = "0 -" + (radioHeight*2) + "px";
						span[a].style.backgroundPosition = position;
					}
				}
				inputs[a].parentNode.insertBefore(span[a], inputs[a]);
				inputs[a].onchange = Custom.clear;
				if(!inputs[a].getAttribute("disabled")) {
					span[a].onmousedown = Custom.pushed;
					span[a].onmouseup = Custom.check;
				} else {
					span[a].className = span[a].className += " disabled";
				}
			}
		}
		inputs = document.getElementsByTagName("select");
		for(a = 0; a < inputs.length; a++) {
			if(inputs[a].className.indexOf("styled") > -1) {
				option = inputs[a].getElementsByTagName("option");
				active = option[0].childNodes[0].nodeValue;
				textnode = document.createTextNode(active);
				for(b = 0; b < option.length; b++) {
					if(option[b].selected == true) {
						textnode = document.createTextNode(option[b].childNodes[0].nodeValue);
					}
				}
				span[a] = document.createElement("span");
				span[a].className = "select";
				span[a].id = "select" + inputs[a].name;
				span[a].appendChild(textnode);
				inputs[a].parentNode.insertBefore(span[a], inputs[a]);
				if(!inputs[a].getAttribute("disabled")) {
					inputs[a].onchange = Custom.choose;
				} else {
					inputs[a].previousSibling.className = inputs[a].previousSibling.className += " disabled";
				}
			}
		}
		document.onmouseup = Custom.clear;
	},
	pushed: function() {
		element = this.nextSibling;
		if(element.checked == true && element.type == "checkbox") {
			this.style.backgroundPosition = "0 -" + checkboxHeight*3 + "px";
		} else if(element.checked == true && element.type == "radio") {
			this.style.backgroundPosition = "0 -" + radioHeight*3 + "px";
		} else if(element.checked != true && element.type == "checkbox") {
			this.style.backgroundPosition = "0 -" + checkboxHeight + "px";
		} else {
			this.style.backgroundPosition = "0 -" + radioHeight + "px";
		}
	},
	check: function() {
		element = this.nextSibling;
		if(element.checked == true && element.type == "checkbox") {
			this.style.backgroundPosition = "0 0";
			element.checked = false;
		} else {
			if(element.type == "checkbox") {
				this.style.backgroundPosition = "0 -" + checkboxHeight*2 + "px";
			} else {
				this.style.backgroundPosition = "0 -" + radioHeight*2 + "px";
				group = this.nextSibling.name;
				inputs = document.getElementsByTagName("input");
				for(a = 0; a < inputs.length; a++) {
					if(inputs[a].name == group && inputs[a] != this.nextSibling) {
						inputs[a].previousSibling.style.backgroundPosition = "0 0";
					}
				}
			}
			element.checked = true;
		}
	},
	clear: function() {
		inputs = document.getElementsByTagName("input");
		for(var b = 0; b < inputs.length; b++) {
			if(inputs[b].type == "checkbox" && inputs[b].checked == true && inputs[b].className.indexOf("styled") > -1) {
				inputs[b].previousSibling.style.backgroundPosition = "0 -" + checkboxHeight*2 + "px";
			} else if(inputs[b].type == "checkbox" && inputs[b].className.indexOf("styled") > -1) {
				inputs[b].previousSibling.style.backgroundPosition = "0 0";
			} else if(inputs[b].type == "radio" && inputs[b].checked == true && inputs[b].className.indexOf("styled") > -1) {
				inputs[b].previousSibling.style.backgroundPosition = "0 -" + radioHeight*2 + "px";
			} else if(inputs[b].type == "radio" && inputs[b].className.indexOf("styled") > -1) {
				inputs[b].previousSibling.style.backgroundPosition = "0 0";
			}
		}
	},
	choose: function() {
		option = this.getElementsByTagName("option");
		for(d = 0; d < option.length; d++) {
			if(option[d].selected == true) {
				document.getElementById("select" + this.name).childNodes[0].nodeValue = option[d].childNodes[0].nodeValue;
			}
		}
	}
}
window.onload = Custom.init;
</script>
<script type="text/javascript">
G = jQuery.noConflict();
G(document).ready( function(){
	G('.proposal_opener').click(function(){
	managerid=G(this).attr('data');
	relval=G(this).attr('rel');
	if(!G(this).hasClass("active")){
	getproposals(managerid,relval);
	G('.proposal_opener').removeClass('active');
	
	G(this).addClass('active');
	}else{
	G('#proposaldetails_'+managerid).slideUp('slow').html('');
	G('#potential_proposaldetails_'+managerid).slideUp('slow').html('');
	G('.table_blue_rowdots_submitted').removeClass('active');
	G(this).removeClass('active');
	
	}
	G('#proposaldetails_'+managerid).removeClass('loader');

});

});

function getproposals(managerid,relval){
G = jQuery.noConflict();
G('#proposaldetails_'+managerid).addClass('loader');
G.post("index2.php?option=com_camassistant&controller=invitations&task=getinsurancestandards", {managerid: ""+managerid+""}, function(data){

if(data) {
if(relval == 'personal'){
	G('#proposaldetails_'+managerid).removeClass('loader');
}
else{
	G('#potential_proposaldetails_'+managerid).removeClass('loader');
}
	G('.prop_details').slideUp();
	G('.table_blue_rowdots_submitted').removeClass('active');
	G('#table_blue_rowdots'+managerid).addClass('active');
if(relval == 'personal'){
	G('#proposaldetails_'+managerid).html(data).slideDown('slow');
}
else{
	G('#potential_proposaldetails_'+managerid).html(data).slideDown('slow');
}

Custom.init();
//G('#getproposals_'+rfpid).hide();
//G('#getproposalshide_'+rfpid).show();
G('#proposaldetails_'+managerid).show();
}
else{
G('#potential_proposaldetails_'+managerid).removeClass('loader');
G('#proposaldetails_'+managerid).removeClass('loader');
G('#proposaldetails_'+managerid).html("No proposals for this RFP");
}
		});
}


function accepted_terms(masterid,vendorid,type){
	G.post("index2.php?option=com_camassistant&controller=vendorscenter&task=gettermsdata", {vendorid: ""+vendorid+"", masterid: ""+masterid+"", type: ""+type+""},function(data){
		if( type == 'acc' ){
			G('#returnmessage_accepted').html(data);
			getgreenpopup();
			}
		else if(type == 'dec'){
			G('#returnmessage_declined').html(data);
			getredpopup();
			}
		else{
			G('#returnmessage_notread').html(data);
			getgraypopup();
		}
		
	});
}

function getredpopup(){
