		G('#maskcl').css({'width':maskWidth,'height':maskHeight});
		G('#maskcl').fadeIn(100);
		G('#maskcl').fadeTo("slow",0.8);
		var winH = G(window).height();
		var winW = G(window).width();
		G("#submitcl").css('top',  winH/2-G("#submitcl").height()/2);
		G("#submitcl").css('left', winW/2-G("#submitcl").width()/2);
		G("#submitcl").fadeIn(2000);
		
		G('.windowcl #donecl').click(function (e) {
		e.preventDefault();
		G('#maskcl').hide();
		G('.windowcl').hide();
