				if(data) {
				location.reload();
				}
				});
		});
		
	 	G('.windowcl #closecl').click(function (e) {
		e.preventDefault();
		G('#maskcl').hide();
		G('.windowcl').hide();
		
		});
	}
	else{
		alert("You must SAVE the document you're currently editing before editing/adding another document");
	}

}
