{

	for(var pln=1; pln<=PLN_title; pln++)

	 {

	   if(document.getElementById('PLN_license'+pln).value == '')	

		 {

		 alert('Please Enter Professional License Number');

		 document.getElementById('PLN_license'+pln).focus();

		 return ;

		 }

		 else if(document.getElementById('PLN_category'+pln).value == '0')	

		 {

		 alert('Please Select Professional License Category');

		 document.getElementById('PLN_category'+pln).focus();

		 return ;

		 }

		 else if(document.getElementById('PLN_type'+pln).value == '0')

		 {

		 alert('Please Select Professional License Type');

		 document.getElementById('PLN_type'+pln).focus();

		 return ;

		 }

		 else if(document.getElementById('PLN_expdate'+pln).value == '')

		 {

		 alert('Please Enter Professional License Expiration Date');

		 document.getElementById('PLN_expdate'+pln).focus();

		 return ;

		 }

		 else if(document.getElementById('PLN_state'+pln).value == '0')

		 {

		 alert('Please Select Professional License State');

		 document.getElementById('PLN_state'+pln).focus();

		 return ;

		 }	

		 else if(document.getElementById('PLN_upld_cert'+pln).value == '')

		{

		  alert('Please Upload Professional License Document');

		 document.getElementById('PLN_upld_cert'+pln).focus();

		 return ;

		}

	   

	 }

}	

 for(var gli=1; gli<=GLI_title; gli++)

 {

