function add_commas(field,x,id)

{

var val = parseFloat(x).toFixed(2)+'',

      rgx = /(\d+)(\d{3}[\d,]*\.\d{2})/;

  while (rgx.test(val)) {

    val = val.replace(rgx, '$1' + ',' + '$2');

  }

  if(val != "NaN")

document.getElementById(field+id).value = val;

}

