<script language="javascript" type="text/javascript">
M = jQuery.noConflict();

function Alt_proposal() {
				//Cancel the link behavior
		//Get the A tag
		//Get the screen height and width
		M('body,html').animate({
scrollTop: 250
},800);
		var maskHeight = M(document).height();
		var maskWidth = M(window).width();
		//Set heigth and width to mask to fill up the whole screen
		M('#mask2').css({'width':maskWidth,'height':maskHeight});
		//transition effect
		M('#mask2').fadeIn(100);
		M('#mask2').fadeTo("slow",0.8);
		//Get the window height and width
		var winH = M(window).height();
		var winW = M(window).width();
		//Set the popup window to center
		//M("#submit2").css('top',  '300');
		//M("#submit2").css('left', '582');
		M("#submit2").css('top',  winH/2-M("#submit2").height()/2);
		M("#submit2").css('left', winW/2-M("#submit2").width()/2);

		M("#submit2").fadeIn(2000);

		M('.window2 #done2').click(function (e) {
		//Cancel the link behavior
		e.preventDefault();
		M('#mask2').hide();
		M('.window2').hide();
