		var maskWidth = G(window).width();
		G('#maskdp').css({'width':maskWidth,'height':maskHeight});
		G('#maskdp').fadeIn(100);
		G('#maskdp').fadeTo("slow",0.8);
		var winH = G(window).height();
		var winW = G(window).width();
		G("#submitdp").css('top',  winH/2-G("#submitdp").height()/2);
		G("#submitdp").css('left', winW/2-G("#submitdp").width()/2);
		G("#submitdp").fadeIn(2000);
		G('.windowdp #donedp').click(function (e) {
		G('#deleteproposalform').submit();
		e.preventDefault();
		G('#maskdp').hide();
		G('.windowdp').hide();
		});
		G('.windowdp #canceldp').click(function (e) {
		e.preventDefault();
		G('#maskdp').hide();
		G('.windowdp').hide();
		});
}
