  function changestate(){
   var Frm = document.VendorForm3;
  var FldLen	= Frm.elements['stateid[]'].length;
	var FldVal	= new Array();
	var TestLen = 0;
	var cnt = 0;
	for(i=0;i<FldLen;i++)
	if(Frm.elements['stateid[]'][i].value != '' )
		FldVal[i] = Frm.elements['stateid[]'][i].value;
	for(j=0;j<FldLen;j++)
	{
		var cnt = In_Array(Frm.elements['stateid[]'][j].value,FldVal);
		if(cnt > 1)
			break;
	}
	if(cnt > 1)
		alert("Already State has been added");
  }
//
function In_Array(value,array) {
	var cnt = 0;
	for(k=0;k<array.length;k++)
	{
		if(array[k] == value)
			cnt++;
	}    
	return cnt;
}
